package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++)
		{
			System.out.println("i value is "+i);
		}
	System.out.println("Decrement the loop");
	
		for(int i=5;i>=1;i--)
		{
			System.out.println("i value is "+i);
		}

	}

}
