package week1.day2;

public class LearnBreakStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Break the execution if the i values goes to 5
		
		for(int i=1;i<=10;i++)
		{
			if(i==5)
			{
				break;
			}
			System.out.println(i);
		}

	}

}
