package week1.day2;

public class LearnElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=70;
		int b=70;
		if(a<b)
		{
			System.out.println("a is smaller than b");
		}
		else if (a>b)
		{
			System.out.println("a is greater than b");
		}
		else
		{
			System.out.println("a & b are equal");
		}
	}

}
